function replaceCharAtMods(e,r){if("number"!=typeof r)throw new Error("The second argument must be a number.");if(r>=10)throw new Error("The second argument must be less than 10.");if(e.length<4)throw new Error("The first argument must be more than 3 characters long.");r=Math.floor(Math.abs(r));const t=Math.floor(e.length/r);let o,n=e.toString();for(let e=1;e<=t;e++)r*e<n.length&&(o=n.split(""),o[r*e]=r,n=o.join(""));return n}module.exports={replaceCharAtMods:replaceCharAtMods};